function RemoveProduct(n){Swal.fire({title:"Are you sure?",text:"You won't be able to revert this!",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Yes, delete it!"}).then(t=>{t.isConfirmed&&$.ajax({url:n,type:"Delete",success:function(n){n.success?(pTable.ajax.reload(),toastr.success(n.messege)):toastr.Error(n.messege)}})})}var pTable;$(document).ready(function(){pTable=$("#productTable").DataTable({ajax:{url:"/Admin/Product/GetData"},columns:[{data:"name"},{data:"description"},{data:"price"},{data:"category.name"},{data:"id",render:function(n){return`
           <a href = "/Admin/Product/AddEditProduct?id=${n}"><i class="bi bi-pencil-square"></i></a>
                    <a href='#' onClick=RemoveProduct("/Admin/Product/Delete?id=${n}") style='cursor:pointer;'><i class="bi bi-trash-fill"></i></a>`}}]})});